mapper.select:
select distinct u.id,u.name
from t_order o
inner join SYS_USER u on o.user_id = u.id
where o.create_time between #{start} and #{end}
and o.del_flag = 0
and u.del_flag = 0;

select count(1)
from t_order o
inner join SYS_USER u on o.user_id = u.id
where o.create_time between #{start} and #{end}
and o.del_flag = 0
and u.del_flag = 0;
